exports.plugins = {
	json:	{
		cache:	false,
		render:	false,
		parse:	function(regex, match, expr, pos, data, callback) {
			if (data[expr]) {
				callback(JSON.stringify(data[expr]));
			} else {
				callback("{}");
			}
		}
	},
	fjson:	{
		cache:	false,
		render:	false,
		parse:	function(regex, match, expr, pos, data, callback) {
			if (data[expr]) {
				callback(JSON.stringify(data[expr], null, 4));
			} else {
				callback("{}");
			}
		}
	},
	text:	{
		cache:	false,
		render:	false,
		parse:	function(regex, match, expr, pos, data, callback) {
			if (data[expr]) {
				callback(data[expr]);
			} else {
				callback("");
			}
		}
	}
}